import './App.css';
import io from 'socket.io-client'
import { useState } from 'react'
//we're connecting our frontend with socket.io (where our backend server where it is runing)
const socket = io.connect('http://localhost:3001')

function App() {
  const [username, setUsername] = useState('');
  const [room, setRoom] = useState('');

  const joinRoom = () => { 
    //we allow to join room without username and room 
    if (username && room) {
      socket.emit("join_room", room)
    }
  }
  return (
    <div className="App">
      <h3>Join A Chat</h3>
      {/* input for name  */}
      <input type="text" placeholder="John..." onChange={(event) => { //event.target.value is whatever we type in inpt
        setUsername(event.target.value)
      }}></input>
      {/* input for room  we want to have chat with peeps in same room  */}
      <input type="text" placeholder="Room-id" onChange={(event) => {
        setRoom(event.target.value)
      }}></input>
      <button onClick={joinRoom}>Join A Room</button>
    </div>
  )
}

export default App;
